import React, {useState, useEffect} from "react";
import Button from '@material-ui/core/Button';
import Checkbox from '@material-ui/core/Checkbox'

import {
    useParams,
    Link
  } from "react-router-dom";

export const ItemDetail = ()=> {
    const { id } = useParams()
    const [data, setData] = useState([])
    const [user, setUser] = useState([])
    const [autobindState, setAutobindState ] = useState([])

    useEffect(()=> {
        fetch(`/item-detail/${id}`)
        .then(response => response.json())
        .then(data => setData(data))
    }, [id])

    useEffect(()=> {
        fetch('/user').then(response=> {
            if(response.ok){
                return response.json()
            }
        }).then(user => setUser(user))
    },[])

    useEffect(()=> {
        fetch(`/get-autobind/${id}`).then(response=> {
            if(response.ok){
                return response.json()
            }
        }).then(autobindState => setAutobindState(autobindState))
    },[id])
    
    const bind = ()=> {
        fetch(`/bind/${id}`,{
            method: 'POST',
            body: JSON.stringify({
                id: id
            })
        }).then(response => response.json())
        window.location.reload();
    }

    const handleAutobindChange = (event) => {
        setAutobindState(!autobindState)
        fetch(`/set-autobind`,{
            method: 'POST',
            body: JSON.stringify({
                id: id,
                state: !autobindState
            })
        }).then(response => response.json())
    }

    return(
        <div>
            {data.length > 0 && data.map(data => 
            <div key='id'>                
                <div>                    
                    <strong>
                        Name:{' '}
                    </strong>
                    {data.name}
                </div>  
                <div>                                      
                    <strong>
                        Description:{' '}
                    </strong>
                    {data.description}                     
                </div>
                <div>
                    <strong>
                        Initial price:{' '}
                    </strong>
                    {data.initial_price+'$'}  
                </div>  
                <div>
                    <strong>
                        Actual bind:{' '}
                    </strong>
                    {data.last_bind_user == null ? '----' : data.last_bind+'$'}
                </div>     
                <div>
                    <strong>
                        Actual user bind:{' '}
                    </strong>
                    {data.last_bind_user == null ? '----' : data.last_bind_user}
                </div>
                <div>
                    <Button variant="contained" color="primary" onClick={bind} disabled={data.last_bind_user === user.user}>
                    BIND
                    </Button>
                </div>
                <div>
                    Auto-bind
                    <Checkbox defaultChecked={autobindState} onChange={handleAutobindChange}></Checkbox>
                </div>      
            </div>
            )}
            
            <hr></hr>
            <Link to='/home'>Back</Link>
        </div>
    )
}
