import React, {useState, useEffect} from "react";
import Button from '@material-ui/core/Button';
import {
    useParams,
    Link
  } from "react-router-dom";

export const ItemDetail = ()=> {
    const { id } = useParams()
    const [data, setData] = useState([])

    useEffect(()=> {
        fetch(`/item-detail/${id}`)
        .then(response => response.json())
        .then(data => setData(data))
    }, [id])

    const bind = ()=> {
        fetch(`/bind/${id}`,{
            method: 'POST',
            body: JSON.stringify({
                id: id
            })
        }).then(response => response.json())
    }

    return(
        <div>
            {data.length > 0 && data.map(data => 
            <div key='id'>                
                <div>                    
                    <strong>
                        Name:{' '}
                    </strong>
                    {data.name}
                </div>  
                <div>                                      
                    <strong>
                        Description:{' '}
                    </strong>
                    {data.description}                     
                </div>
                <div>
                    <strong>
                        Initial price:{' '}
                    </strong>
                    {data.initial_price}  
                </div>  
                <div>
                    <strong>
                        Actual bind:{' '}
                    </strong>
                    {data.last_bind_user == null ? 'None' : data.last_bind}
                </div>     
                <div>
                    <strong>
                        Actual user bind:{' '}
                    </strong>
                    {data.last_bind_user == null ? 'None' : data.last_bind_user}
                </div>        
                <Button variant="contained" color="primary" onClick={bind}>BIND</Button>
                    
            </div>
            )}
            
            <hr></hr>
            <Link to='/'>Back</Link>
        </div>
    )
}
