import React from "react";

export const GlobalFilter = ({filter, setFilter}) => {
    return (
        <div className="input-group mb-3">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Search by title"
                        value={filter || ''} 
                        onChange={(e) => setFilter(e.target.value)}
                    />
                    </div>

    )
}
