import React, {useState, useEffect} from "react";
import {
    useParams,
    Link
  } from "react-router-dom";

export const ItemDetail = ()=> {
    const { id } = useParams()
    const [data, setData] = useState([])

    useEffect(()=> {
        fetch(`/item-detail/${id}`)
        .then(response => response.json())
        .then(data => setData(data))
    }, [id])

    return(
        <div>
            {data.length > 0 && data.map(data => 
            <div key='id'>                
                <div>                    
                    <strong>
                        Name:{' '}
                    </strong>
                    {data.name}
                </div>  
                <div>                                      
                    <strong>
                        Description:{' '}
                    </strong>
                    {data.description}                     
                </div>
                <div>
                    <strong>
                        Initial price:{' '}
                    </strong>
                    {data.initial_price}  
                </div>  
                <div>
                    <strong>
                        Actual bind:{' '}
                    </strong>
                    {data.last_bind}  
                </div>     
                <div>
                    <strong>
                        Actual user bind:{' '}
                    </strong>
                    {data.last_bind_user == Null? data.last_bind_user :  'None'}
                </div>        
                    
            </div>
            )}
            
            <hr></hr>
            <Link to='/'>Back</Link>
        </div>
    )
}
